import React, {useState, useEffect} from 'react';
import { Line } from 'react-chartjs-2'
import moment from "moment";


const Graph = () => {
    const data = {
        datasets: [
          {
            label: "Dataset 1 (linear interpolation)",
            backgroundColor: color(chartColors.red)
              .alpha(0.5)
              .rgbString(),
            borderColor: chartColors.red,
            fill: false,
            lineTension: 0,
            borderDash: [8, 4],
            data: []
          }
        ]
      };
      const options = {
        elements: {
          line: {
            tension: 0.5
          }
        },
        scales: {
          xAxes: [
            {
              type: "realtime",
              distribution: "linear",
              realtime: {
                onRefresh: function(chart) {
                  chart.data.datasets[0].data.push({
                    x: moment(),
                    y: Math.random()
                  });
                },
                delay: 3000,
                time: {
                  displayFormat: "h:mm"
                }
              },
              ticks: {
                displayFormats: 1,
                maxRotation: 0,
                minRotation: 0,
                stepSize: 1,
                maxTicksLimit: 30,
                minUnit: "second",
                source: "auto",
                autoSkip: true,
                callback: function(value) {
                  return moment(value, "HH:mm:ss").format("mm:ss");
                }
              }
            }
          ],
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
                max: 1
              }
            }
          ]
        }
      };

    return ( 
        <div>

        </div>
     );
}

export default Graph;
