import React from 'react';
import { Switch, Route, Link, useLocation, Redirect, useHistory} from "react-router-dom";
import { Avatar, Spin, Button } from 'antd';
import './styles.css'

const Login = () => {
    const history = useHistory()
    const handleLogin = (e) => {
        history.push('/overview')
    }
    return ( 
        <div style={{background:'#2f3136', height:'100vh', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center', width:'100%'}}>
            <div style={{background:'#3A3C41', borderRadius:'20px', height:'300px', width:'300px', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center', boxShadow:'0px 0px 20px 1px #202225'}}>
                <div className="brand">
                    <img src="DanceOnLogo.svg" style={{height:'40px'}}/>
                    <div style={{marginLeft:'10px'}}>DANCE<span style={{fontWeight:'bold', color:'#5a65ea'}}>DANCE</span></div> 
                </div>
                <Button type="primary" onClick={handleLogin} >Login</Button>
            </div>
        </div>
     );
}

export default Login;
