import React from "react";
import { render } from "react-dom";

import { LineChart, Line, XAxis, YAxis, ReferenceLine, AreaChart, Area, Tooltip} from "recharts";

const styles = {
  fontFamily: "sans-serif",
  textAlign: "center"
};

const data = [];
const launchDate = 2004;

const rand = 300;
for (let i = 0; i < 7; i++) {
  const year = 2000 + i;
  const value = Math.random() * (rand + 50) + 100;
  let d = {
    year: year,
    value: value
  };

  data.push(d);
}

// change type to see that the overlap might not be appropriate towards the
// end of the shorter chart
const type = "monotone";

// calculate percentage for launchDate - index = 4, total = 7
const percentage = 100 - ((7 - 4 - 1) / (7 - 1)) * 100;

const SyncGraph = () => {
    return ( 
        <div style={styles}>
            <AreaChart
            width={500}
            height={300}
            data={data}
            margin={{ top: 5, right: 20, bottom: 5, left: 0 }}
            >
            <defs>
                <linearGradient id="gradient" x1="0" y1="0" x2="100%" y2="0">
                <stop offset="0%" stopColor="black" />
                <stop offset={`${percentage}%`} stopColor="#5A65EA" stopOpacity={0.9} />
                <stop offset={`${percentage}%`} stopColor="#ff6d98" stopOpacity={0.2}/>
                <stop offset="100%" stopColor="#ff6d98" />
                </linearGradient>

            </defs>
            <Tooltip />
            <Area dataKey="value" fill="url(#gradient)" stroke="url(#gradient)"/>
            {/* <Line type={type} dataKey="value" stroke="url(#gradient)" dot={false} /> */}
            <XAxis dataKey="year" />
            <YAxis />
            <ReferenceLine x={launchDate} label="iPhone" />
            </AreaChart>
        </div>
     );
}

export default SyncGraph;
