import React, {useState, useEffect} from 'react';
import { Modal, Button, Table, Avatar, Tag, Space, Input, Select} from 'antd'
import {UserOutlined} from '@ant-design/icons';

import * as api from '../api/index'

const columns = [
    {
      title: 'User',
      dataIndex: 'username',
      key: 'name',
      render: text => <div style={{color:'white', display:'flex', flexDirection:'row', alignItems:'center'}}><div style={{width:'100%'}}>{text} </div> </div>,
    },
    {
      dataIndex: 'image',
      key: 'image',
      render: text => <Avatar src={text}/>

    },
    {
        title: 'Wearable Name',
        dataIndex: 'wearable_name',
        key: 'wearable_name',
        render: text => 
            <>
              <Tag color={text == "Dance Band Pro" ? "pink" : (text == "Dance Band Lite" ? "green" : null)} style={{backgroundColor:"transparent", color:'white'}}>
                {text}
              </Tag>
            </>
      },
      {
        title: 'Wearable ID',
        dataIndex: 'wearable_id',
        key: 'wearable_id',
        render: text => 
            <>
              <div>{text}</div>
            </>
      },
    {
      title: 'Action',
      key: 'action',
      render: (text, record) => (
        <Space size="middle">
          <a>View</a>
          {/* <a style={{color:'red'}}>Delete</a> */}
        </Space>
      ),
    },
  ];


const NewSession = ({userList, test}) => {
    // const [visible, setVisible] = useState(isVisible)
    // const handleClose = () => {
    //     setVisible(false)
    // }
    const [users, setUsers] = useState(null)
    const [sessionName, setSessionName] = useState('')
    const [loading, setLoading] = useState(false)

    const handleSessionNameChange = (e) => {
        setSessionName(e.target.value)
    }
    const getUsers = async() => {
        try {
            setLoading(true)
            // dispatch(getusers())
            // setUsers(user_list)
            // console.log(dispatch({type: 'GET_USERS'}))
            // setUsers(dispatch({type: 'GET_USERS'}))
            await api.getUsers().then(users => {
              console.log(users)
              setUsers(users.data)
            })

          } catch (error) {
            alert(error)
          }
          finally{
            setLoading(false)
          }
    }
    useEffect(() => {
        getUsers()
    }, [])
    return ( 
        <div style={{height:'100%', width:'100%'}}>
            {test}
            <div>
                <h3 style={{color:'white'}}>New Session</h3>
                <h5 style={{color:'white'}}>Select Users</h5>
            </div>
            <Input name="name" size="large" 
            value={sessionName}
            onChange={handleSessionNameChange}
            placeholder="session" 
            style={{width:'90%', marginTop:'10px', borderRadius:'8px', border:'transparent', background:'#2f3136', marginBottom:'0px'}}
            />
            <div style={{borderRadius:'20px', backgroundColor:'#3A3C41'}}>
            <Table 
                        // style={{width:'500px'}}
                        pagination={false}
                        columns={columns} 
                        dataSource={users} 
                        />
            </div>

            <div style={{height:'100%', maxHeight:'60vh',borderRadius:'20px', overflow:'scroll',  boxShadow: '0px 0px 20px 1px #202225', marginTop:'20px', backgroundColor:'#3A3C41', width:'100%'}}>

                    <div style={{borderRadius:'20px', backgroundColor:'#3A3C41', padding:'20px', marginTop:'0px', width:'100%'}} >
                        <div style={{marginLeft:'10px', fontSize:'20px', marginBottom:'10px', display:'flex', flexDirection:'row', alignItems:'center', justifyContent:'space-between'}}>
                            <div style={{display:'flex', alignItems:'center'}}>
                                All Users <UserOutlined style={{marginLeft:'10px'}}/>
                            </div>
                        </div>

                    </div>

            </div>

        </div>
     );
}

export default NewSession;
